    BOOST_CHECK(merkleBlock.vMatchedTxn[0].second == uint256S("0xf02f194ebe0d015dfb5ce409ef5265824bbd9ac1b969c8ead5d687ad42859dc8"));
    BOOST_CHECK(merkleBlock.vMatchedTxn[0].first == 0);

    std::vector<uint256> vMatched;
